import React, { useRef } from "react";
import "./App.css";

interface CanvasProps {
  width: number;
  height: number;
}

function App({ width, height }: CanvasProps) {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  return (
    <div className="App">
      <canvas
        ref={canvasRef}
        height={height}
        width={width}
        className="canvas"
      />
    </div>
  );
}

export default App;
